import './App.css';
import React from 'react';
import LoginPage from './login';

class App extends React.Component {
  constructor() {
    super();
  }

 onLoginPageSubmit(username,password,url){
    console.log("hello 2");
    console.log(username+" "+password+" "+url);
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    var request = new XMLHttpRequest();
    var form = new FormData();
    form.append("password", password);
    form.append("username", username);
    request.open("POST", url + "/login");
    console.log(request);
    request.onreadystatechange = function(){
        if (request.readyState !== 4) return;
        if (request.status === 201) {
            sessionStorage.setItem( 'token', JSON.parse(request.responseText).token);
            //sessionStorage.setItem( 'currentScreen',"ChatScreen");
            //sessionStorage.setItem( 'currentUsername',username);
            /*this.setState({
            currentUsername: username,
            currentScreen: sessionStorage.getItem( 'currentScreen'),
            token: sessionStorage.getItem( 'token' )
            });*/
        } else if (this.status === 403) {
            alert("Invalid username or password");
        } else {
            alert(this.status + " failure to /login");
        }
    }.bind(this);
    request.send(form); 
 }

 render(){
	console.log("hello")
	return (
	    <div className="App">
			<LoginPage onSubmit={this.onLoginPageSubmit} />
	    </div>
	  );
}

}

export default App;
