import React, { Component } from 'react';
import SendMessageBar from './SendMessageBar';
import './App.css';

class ChatPage extends Component{
	constructor(props) {
		super(props);
		this.state = {
		      allMessages: [],
		      token: this.props.token,
		      currentUser: this.props.username,
		      allUsers: [],
		      url: this.props.url

		};
	this.sendingMessage=this.sendingMessage.bind(this);
}
sendingMessage(message){
	console.log("Msg received is ",message)
}
render(){
		return(
			<div className="ChatPage">
				<div className="chatHeader">
						<h1 className="ChatHeader"> Hey, {this.state.currentUser}, you are online! Chat with your friends </h1>
				</div>
				<div className="Message-input">
						<SendMessageBar onSubmit={this.sendingMessage} />
				</div>	

			</div>
		     )		
	}


}
export default ChatPage
