import React from 'react'
import './App.css';

class Userlogin extends React.Component{
	constructor(props){
		super(props)
		this.state = {
			username: '',
			password: '',
			url: this.props.url
		}
		this.onChangeUsername = this.onChangeUsername.bind(this)
		this.onChangePwd = this.onChangePwd.bind(this)
		this.onSubmit = this.onSubmit.bind(this)
		this.onChangeUrl = this.onChangeUrl.bind(this)
	}
	onChangeUrl(e){
		this.setState({
			url: e.target.value
		})
	}
	onChangeUsername(e){
		this.setState({
			username: e.target.value
		})
	}
	onChangePwd(e){
		this.setState({
			password: e.target.value
		})
	}
	onSubmit (e){
		e.preventDefault()
		this.props.onSubmit(this.state.username,this.state.password, this.state.url)
	}
	render(){
		return <div>
		
		<form className="login-form" onSubmit={this.onSubmit}>
		<h2>Please enter your details</h2> <br/>
		<table >
			<tr>
				<td>USERNAME:</td>
				<td><input type = "text" placeholder="Enter Username" value={this.state.username} onChange={this.onChangeUsername}/></td>
			</tr>
			<tr>
				<td>PASSWORD:</td>
				<td><input type = "password" placeholder="Enter Password" value={this.state.password} onChange={this.onChangePwd}/></td>
			</tr>
			<tr>
				<td>URL</td>
				<td><input type = "text" value={this.state.url} placeholder="Enter network URL" onChange={this.onChangeUrl}/></td>
			</tr>
			<tr>
				<td><input type = "submit" value="Press to submit!!!"/></td>
			</tr>
			</table> 			
		</form>
		</div>
	}
}

export default Userlogin
